import React, { Component } from 'react';
import { DayPilotScheduler } from "daypilot-pro-react";
import 'react-big-scheduler/lib/css/style.css'
import myScheduler, { SchedulerData, ViewTypes, DATE_FORMAT } from 'react-big-scheduler'
import moment from 'moment'

let schedulerData = new SchedulerData(new moment().format(DATE_FORMAT), ViewTypes.Week);

class Scheduler extends Component {

    constructor(props) {
        super(props);

        moment.locale('zh-cn');
        schedulerData.setLocaleMoment(moment);

        this.state = {
            startDate: "2018-05-01",
            days: 31,
            scale: "Day",
            eventHeight: 30,
            cellWidth: 50,
            timeHeaders: [
                { groupBy: "Month" },
                { groupBy: "Day", format: "d" }
            ],
            //shell width
            cellWidthSpec: "50",
            //display timing 
            resources: [
                { name: "9:00 AM", id: "A" },
                { name: "10:00 AM", id: "B" },
                { name: "11:00 AM", id: "C" },
                { name: "12:00 PM", id: "D" },
                { name: "1:00 PM", id: "E" },
                { name: "2:00 PM", id: "F" },
                { name: "3:00 PM", id: "G" },
                { name: "4:00 PM", id: "H" },
                { name: "5:00 PM", id: "I" },
                { name: "6:00 PM", id: "J" }
            ],
            //day appoinment
            events: [
                { id: 1, text: "Bob", start: "2018-05-02T00:00:00", end: "2018-05-02T00:00:00", resource: "A" },
                { id: 2, text: "Sara", start: "2018-05-03T00:00:00", end: "2018-05-03T00:00:00", resource: "C", barColor: "#38761d", barBackColor: "#93c47d" },
                { id: 3, text: "Mark", start: "2018-05-02T00:00:00", end: "2018-05-02T00:00:00", resource: "D", barColor: "#f1c232", barBackColor: "#f1c232" },
                { id: 3, text: "Dylan", start: "2018-05-04T00:00:00", end: "2018-05-04T00:00:00", resource: "E", barColor: "#cc0000", barBackColor: "#ea9999" },
                { id: 4, text: "Bob", start: "2018-05-02T00:00:00", end: "2018-05-02T00:00:00", resource: "F" },
                { id: 5, text: "Sara", start: "2018-05-03T00:00:00", end: "2018-05-03T00:00:00", resource: "G", barColor: "#38761d", barBackColor: "#93c47d" },
                { id: 6, text: "Mark", start: "2018-05-04T00:00:00", end: "2018-05-04T00:00:00", resource: "H", barColor: "#f1c232", barBackColor: "#f1c232" },
                { id: 7, text: "Dylan", start: "2018-05-05T00:00:00", end: "2018-05-05T00:00:00", resource: "I", barColor: "#cc0000", barBackColor: "#ea9999" }
            ]
        };
        schedulerData.setResources(this.state.resources);
        // schedulerData.setEvents(this.state.events);
    }

    render() {
        var { ...config } = this.state;
        const {schedulerData} = this.state;
        return (
            <div>
                <DayPilotScheduler
                    {...config}
                />
                <Scheduler schedulerData={schedulerData}
                    prevClick={this.prevClick}
                    nextClick={this.nextClick}
                    onSelectDate={this.onSelectDate}
                    onViewChange={this.onViewChange}
                    eventItemClick={this.eventClicked}
                />
            </div>
        );
    }
}

export default Scheduler;
